import osimport globimport pandas as pdfrom collections import Counterimport docx2txtsrc='./DulieuTapchi/EN'obj_list=glob.glob(src+"/**",recursive=True)new_list = []list_info = []list_category = []for path in obj_list:    new_list.append(path.strip('./DulieuTapchi/EN'))for info in new_list:    info = info.split('/', 3)    del info[0:2]    # list -> str    string = " ".join(map(str, info))    # get informations    if string != '':        # print(string)        info = string.split('-',2)        for i in info:            list_info.append(i)# print(list_info)for category in list_info:     if category.isalpha():         list_category.append(category)# print(list_category)Counts = Counter(list_category)a = pd.Series(Counts)a = a.to_dict()df = pd.DataFrame.from_dict(a,orient='index',columns=['Total'])df.to_numpy()# print(df)#============================================= GET CONTENT IN FILEos.chdir(src+'/2019/Vol 11-3/')listdir = os.listdir()# print(listdir)corpus = docx2txt.process('09-ET-LE THANH PHONG(65-74)040.docx')#xóa các dòng xuống hàngfullText = []for para in corpus.split("\n"):    if len(para)>3:        fullText.append(para)# lấy tiêu đềgetTitle = []for title in fullText:    index_ = title.find('DOI')    if index_ == 0:        index_title = fullText[index_+2]        getTitle.append(index_title)# print(getTitle)# lấy các đề mục chínhmainIndex = []for index in fullText:    if index.isupper():        mainIndex.append(index)print(mainIndex)# tìm vị trí keyword(TITLE - ABSTRACT - CONCLUSION - REFERENCES) trong file txtprint("TITLE:", str(getTitle))sub_index_Abstract = corpus.find('ABSTRACT')# print("Vị trí từ ABSTRACT nằm ở dòng: ", sub_index_Abstract)sub_index_Conclusion = corpus.find('CONCLUSIONS')# print("Vị trí từ CONCLUSIONS nằm ở dòng: ", sub_index_Conclusion)sub_index_Reference = corpus.find('REFERENCES')# print("Vị trí từ REFERENCES nằm ở dòng: ", sub_index_Reference)# Chỉ lấy nội dung của ABSTRACT: từ vị trí ABSTRACT -> vị trí của INTRODUCTION    #xác định vị trí của Introductionsub_index_INTRODUCTION = corpus.find('INTRODUCTION')print('Vị trí từ INTRODUCTION nằm ở dòng:',sub_index_INTRODUCTION)    # Lấy nội dung Abstractcontent_Abstract = corpus[sub_index_Abstract:sub_index_INTRODUCTION]# print(content_Abstract)# Chỉ lấy nội dung của CONCLUSIONS: từ vị trí CONCLUSIONS -> vị trí của ACKNOWLEDGMENTS    #xác định vị trí của Introductionsub_index_Acknowlegment = corpus.find('ACKNOWLEDGMENTS')print('Vị trí từ ACKNOWLEDGMENTS nằm ở dòng:',sub_index_Acknowlegment)    # Lấy nội dung Conclusioncontent_Conclusion = corpus[sub_index_Conclusion:sub_index_Acknowlegment]# print(content_Conclusion)# Chỉ lấy nội dung của REFERENCES: từ vị trí REFERENCES -> hếtcontent_Reference = corpus[sub_index_Reference:]# print(content_Reference)print(str(getTitle) + content_Abstract + content_Conclusion + content_Reference)